import React, { useState } from 'react';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true); // true — авторизация, false — регистрация
  const [formData, setFormData] = useState({ username: '', password: '' });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isLogin ? '/api/auth/login' : '/api/auth/register';
    try {
      const response = await axios.post("http://localhost:5000" + endpoint, {username, password});
      const data = await response.json();
      if (!response.ok) throw new Error(data.message || 'Ошибка');
      alert(isLogin ? 'Вы успешно вошли!' : 'Регистрация прошла успешно!');
    } catch (error) {
      alert(`Ошибка: ${error.message}`);
    }
  };

  return (
    <div style={containerStyle}>
      <h2>{isLogin ? 'Авторизация' : 'Регистрация'}</h2>
      <form onSubmit={handleSubmit} style={formStyle}>
        <input
          type="text"
          name="username"
          placeholder="Имя пользователя"
          value={formData.username}
          onChange={handleInputChange}
          required
          style={inputStyle}
        />
        <input
          type="password"
          name="password"
          placeholder="Пароль"
          value={formData.password}
          onChange={handleInputChange}
          required
          style={inputStyle}
        />
        <button type="submit" style={buttonStyle}>
          {isLogin ? 'Войти' : 'Зарегистрироваться'}
        </button>
      </form>
      <p style={toggleStyle}>
        {isLogin ? 'Нет аккаунта?' : 'Уже есть аккаунт?'}
        <button onClick={() => setIsLogin(!isLogin)} style={linkStyle}>
          {isLogin ? 'Зарегистрируйтесь' : 'Войдите'}
        </button>
      </p>
    </div>
  );
};

const containerStyle = {
  maxWidth: '400px',
  margin: '50px auto',
  padding: '20px',
  border: '1px solid #ccc',
  borderRadius: '8px',
  textAlign: 'center',
  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
};

const formStyle = {
  display: 'flex',
  flexDirection: 'column',
  gap: '15px',
};

const inputStyle = {
  padding: '10px',
  fontSize: '16px',
  border: '1px solid #ccc',
  borderRadius: '4px',
};

const buttonStyle = {
  padding: '10px',
  fontSize: '16px',
  color: '#fff',
  backgroundColor: '#007BFF',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
};

const toggleStyle = {
  marginTop: '10px',
};

const linkStyle = {
  marginLeft: '5px',
  color: '#007BFF',
  background: 'none',
  border: 'none',
  cursor: 'pointer',
  textDecoration: 'underline',
};

export default AuthForm;
