import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AuthForm from './AuthForm';

const AdminPanel = () => {
  const [authToken, setAuthToken] = useState(sessionStorage.getItem('token'));
  const [calculations, setCalculations] = useState([]);

  useEffect(() => {
    const fetchCalculations = async () => {
      try {
        console.log(authToken);
        const response = await axios.get('http://localhost:5000/api/admin/calculations', {
          headers: { Authorization: `Bearer ${authToken}` },
        });
        setCalculations(response.data);
      } catch (error) {
        sessionStorage.removeItem('token');
        setAuthToken(authToken);
      }
    };
    if (authToken) fetchCalculations();
  }, [authToken]);

  if (!authToken) {
    return <AuthForm setAuthToken={setAuthToken} />;
  }

  const containerStyle = {
    maxWidth: '800px',
    margin: '50px auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '8px',
    textAlign: 'center',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  };

  const tableStyle = {
    width: '100%',
    borderCollapse: 'collapse',
    marginTop: '20px',
  };

  const thStyle = {
    padding: '10px',
    backgroundColor: '#f2f2f2',
    borderBottom: '2px solid #ccc',
    textAlign: 'left',
  };

  const tdStyle = {
    padding: '10px',
    borderBottom: '1px solid #ccc',
  };

  const resultStyle = {
    marginTop: '20px',
    fontSize: '18px',
    color: 'green',
  };

  return (
    <div style={containerStyle}>
\      <table style={tableStyle}>
        <thead>
          <tr>
            <th style={thStyle}>Тип</th>
            <th style={thStyle}>Ставка</th>
            <th style={thStyle}>Результат</th>
            <th style={thStyle}>Email</th>
          </tr>
        </thead>
        <tbody>
          {calculations.map((calc) => (
            <tr key={calc._id}>
              <td style={tdStyle}>{calc.type}</td>
              <td style={tdStyle}>{calc.rate}%</td>
              <td style={tdStyle}>{calc.result} руб</td>
              <td style={tdStyle}>{calc.email}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AdminPanel;
