import React, { useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import Header from './components/Header';
import MortgageCalculator from './components/MortgageCalculator';
import AutoLoanCalculator from './components/AutoLoanCalculator';
import AdminPanel from './components/AdminPanel';
import AuthForm from './components/AuthForm';
import ConsumerLoanCalculator from './components/ConsumerLoanCalculator';

function App() {
  const [authToken, setAuthToken] = useState(localStorage.getItem('token') || '');

  const handleLogout = () => {
    localStorage.removeItem('token');
    setAuthToken('');
  };

  return (
    <div>
      <Header authToken={authToken} onLogout={handleLogout} />
      <Routes>
        {!authToken ? (
          <Route path="/login" element={<AuthForm setAuthToken={setAuthToken} />} />
        ) : (
          <>
            <Route path="/" element={<MortgageCalculator />} />
            <Route path="/auto-loan" element={<AutoLoanCalculator />} />
            <Route path="/admin" element={<AdminPanel />} />
            <Route path="/consumer-loan" element={<ConsumerLoanCalculator />} />
          </>
        )}
        {/* Перенаправление на страницу авторизации для всех неизвестных путей */}
        <Route path="*" element={<AuthForm setAuthToken={setAuthToken} />} />
      </Routes>
    </div>
  );
}

export default App;
