import React, { useState } from 'react';
import axios from 'axios';

const ConsumerLoanCalculator = () => {
   const [formData, setFormData] = useState({
      principal: '',
      interestRate: '',
      loanTerm: '',
      email: '',
   });
   const [monthlyPayment, setMonthlyPayment] = useState(null);

   const handleInputChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
   };

   const handleSubmit = async (e) => {
      e.preventDefault();
      try {
         const response = await axios.post('http://localhost:5000/api/consumer-loan/calculate', {
            type: 'consumer-loan',
            rate: formData.interestRate,
            principal: formData.principal,
            duration: formData.loanTerm,
            email: formData.email,
         });
         setMonthlyPayment(response.data.monthlyPayment);
      } catch (error) {
         alert('Ошибка при расчете. Проверьте данные.');
      }
   };

   return (
      <div style={containerStyle}>
         <h2>Калькулятор потребительского кредита</h2>
         <form onSubmit={handleSubmit} style={formStyle}>
            <input
               type="number"
               name="principal"
               placeholder="Сумма кредита"
               value={formData.principal}
               onChange={handleInputChange}
               required
               style={inputStyle}
            />
            <input
               type="number"
               name="interestRate"
               placeholder="Процентная ставка (%)"
               value={formData.interestRate}
               onChange={handleInputChange}
               required
               style={inputStyle}
            />
            <input
               type="number"
               name="loanTerm"
               placeholder="Срок кредита (лет)"
               value={formData.loanTerm}
               onChange={handleInputChange}
               required
               style={inputStyle}
            />
            <input
               type="email"
               name="email"
               placeholder="Ваш email"
               value={formData.email}
               onChange={handleInputChange}
               required
               style={inputStyle}
            />
            <button type="submit" style={buttonStyle}>Рассчитать</button>
         </form>
         {monthlyPayment !== null && (
            <p style={resultStyle}>Ежемесячный платеж: {monthlyPayment.toFixed(2)} руб.</p>
         )}
      </div>
   );
};

const containerStyle = {
   maxWidth: '400px',
   margin: '50px auto',
   padding: '20px',
   border: '1px solid #ccc',
   borderRadius: '8px',
   textAlign: 'center',
   boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
};

const formStyle = {
   display: 'flex',
   flexDirection: 'column',
   gap: '15px',
};

const inputStyle = {
   padding: '10px',
   fontSize: '16px',
   border: '1px solid #ccc',
   borderRadius: '4px',
};

const buttonStyle = {
   padding: '10px',
   fontSize: '16px',
   color: '#fff',
   backgroundColor: '#007BFF',
   border: 'none',
   borderRadius: '4px',
   cursor: 'pointer',
};

const resultStyle = {
   marginTop: '20px',
   fontSize: '18px',
   color: 'green',
};

export default ConsumerLoanCalculator;
