import React from 'react';
import { Link } from 'react-router-dom';

const Header = ({ authToken, onLogout }) => {
  return (
    <header style={headerStyle}>
      <nav style={navStyle}>
        {authToken ? (
          <>
            <Link to="/" style={linkStyle}>Ипотека</Link>
            <Link to="/consumer-loan" style={linkStyle}>Автокредит</Link>
            <Link to="/auto-loan" style={linkStyle}>Потребительский кредит</Link>
            <Link to="/admin" style={linkStyle}>Админ Панель</Link>
            <button onClick={onLogout} style={logoutButtonStyle}>Выйти</button>
          </>
        ) : (
          <Link to="/login" style={linkStyle}>Авторизация</Link>
        )}
      </nav>
    </header>
  );
};

const headerStyle = {
  background: '#007BFF',
  padding: '10px',
  color: 'white',
  textAlign: 'center',
};

const navStyle = {
  display: 'flex',
  justifyContent: 'center',
  gap: '20px',
};

const linkStyle = {
  color: 'white',
  textDecoration: 'none',
  fontWeight: 'bold',
};

const logoutButtonStyle = {
  backgroundColor: '#ff4d4d',
  color: 'white',
  border: 'none',
  padding: '10px 15px',
  borderRadius: '4px',
  cursor: 'pointer',
  fontWeight: 'bold',
};

export default Header;
