import React, { useState } from 'react';
import axios from 'axios';

const MortgageCalculator = () => {
  const [principal, setPrincipal] = useState(2000000);
  const [downPayment, setDownPayment] = useState(500000);
  const [duration, setDuration] = useState(20);
  const [rate, setRate] = useState(9.6);
  const [monthlyPayment, setMonthlyPayment] = useState(null);
  const [email, setEmail] = useState(null);

  const calculateMortgage = async () => {
    const response = await axios.post('http://localhost:5000/api/calculate', {
      type: 'mortgage',
      rate,
      principal,
      downPayment,
      duration,
    });
    setMonthlyPayment(response.data.monthlyPayment);
  };

  const containerStyle = {
    maxWidth: '400px',
    margin: '50px auto',
    padding: '20px',
    border: '1px solid #ccc',
    borderRadius: '8px',
    textAlign: 'center',
    boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px',
  };

  const inputStyle = {
    padding: '10px',
    fontSize: '16px',
    border: '1px solid #ccc',
    borderRadius: '4px',
  };

  const buttonStyle = {
    padding: '10px',
    fontSize: '16px',
    color: '#fff',
    backgroundColor: '#007BFF',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  };

  const resultStyle = {
    marginTop: '20px',
    fontSize: '18px',
    color: 'green',
  };

  return (
    <div style={containerStyle}>
      <h2>Ипотечный калькулятор</h2>
      <div style={formStyle}>
        <input
          type="number"
          value={principal}
          onChange={(e) => setPrincipal(e.target.value)}
          placeholder="Стоимость"
          style={inputStyle}
        />
        <input
          type="number"
          value={downPayment}
          onChange={(e) => setDownPayment(e.target.value)}
          placeholder="Первоначальный взнос"
          style={inputStyle}
        />
        <input
          type="number"
          value={duration}
          onChange={(e) => setDuration(e.target.value)}
          placeholder="Срок (лет)"
          style={inputStyle}
        />
        <input
          type="text"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Почта"
          style={inputStyle}
        />
        <button onClick={calculateMortgage} style={buttonStyle}>
          Рассчитать
        </button>
      </div>
      {monthlyPayment && <p style={resultStyle}>Ежемесячный платеж: {monthlyPayment} руб</p>}
    </div>
  );
};

export default MortgageCalculator;
