import React, { useState } from 'react';
import axios from 'axios';

const MortgageCalculator = () => {
   const [principal, setPrincipal] = useState(2000000);
   const [downPayment, setDownPayment] = useState(500000);
   const [duration, setDuration] = useState(20);
   const [rate, setRate] = useState(9.6);
   const [monthlyPayment, setMonthlyPayment] = useState(null);

   const calculateMortgage = async () => {
      const response = await axios.post('http://localhost:5000/api/calculate', {
         type: 'mortgage',
         rate,
         principal,
         downPayment,
         duration,
      });
      setMonthlyPayment(response.data.monthlyPayment);
   };

   return (
      <div>
         <h2>Mortgage Calculator</h2>
         <input type="number" value={principal} onChange={(e) => setPrincipal(e.target.value)} placeholder="Стоимость" />
         <input type="number" value={downPayment} onChange={(e) => setDownPayment(e.target.value)} placeholder="Первоначальный взнос" />
         <input type="number" value={duration} onChange={(e) => setDuration(e.target.value)} placeholder="Срок (лет)" />
         <button onClick={calculateMortgage}>Рассчитать</button>
         {monthlyPayment && <p>Ежемесячный платеж: {monthlyPayment} руб</p>}
      </div>
   );
};

export default MortgageCalculator;
